  selector: 'app-img',
  templateUrl: './img.component.html',
  styleUrls: ['./img.component.css']
})
export class ImgComponent {
  getTypeFileList: string[] |undefined;
  getFileList:string[] |undefined;

  imgsList:string[]=['C:/Users\IMOE001\Desktop\nechama\img\m4.',
                     'C:\Users\IMOE001\Desktop\nechama\img\m4.JPG',
                     'C:\Users\IMOE001\Desktop\nechama\img\m4.JPG'];

  constructor(private myServe:MySerService){}



