import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MatSidenavModule} from '@angular/material/sidenav';
import { distinctUntilChanged, map } from 'rxjs';
import { MySerService } from '../../Service/my-ser.service';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent {

  location:string="";
  loadAfterGetLocation = false;

  constructor(private myServe:MySerService, private router: Router,private route:ActivatedRoute) {}


  ngOnInit() {
    alert(`you need to insert in this format -/homePage?path= yo`);
      //שמירת הנתונים מה/URL/במשתנה
      this.route.queryParams.pipe(
        map(params => params["path"]),
        distinctUntilChanged(),
      ).subscribe(changedParam => {
        alert(`reacting to single param change ${changedParam}`);
        this.location = changedParam;
        this.loadAfterGetLocation = true;

        // this.sendlocation(changedParam);
      })
  }

