import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MatSidenavModule} from '@angular/material/sidenav';
import { SomeJpgFilesComponent } from "../some-jpg-files/some-jpg-files.component";
import { ListBarComponent } from "../list-bar/list-bar.component";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  standalone: true,
  imports: [MatSidenavModule, ListBarComponent],
  template: 'The href is: {{href}}',
})


export class HomeComponent {
  public href: string = "";

  constructor(private router: Routerprivate route:ActivatedRoute) {}


  this.route.params.pipe(
    map(params => params.yourParamName),
    distinctUntilChanged(),
  ).subscribe(changedParam => console.log(`reacting to single param change ${changedParam}`));
  ngOnInit() {


      this.href = this.router.url;
      console.log(this.router.url);
  }
}
