import {FlatTreeControl} from '@angular/cdk/tree';
import {Component} from '@angular/core';
import {MatTreeFlatDataSource, MatTreeFlattener, MatTreeModule} from '@angular/material/tree';
import {MatIconModule} from '@angular/material/icon';
import {MatButtonModule} from '@angular/material/button';
import { ActivatedRoute } from '@angular/router';
import { MySerService } from '../../Service/';

//הגדרת הרשימה
interface allListFile {
  name: string;
  children?: allListFile[];
}
//רשימה זמנית
const TREE_DATA: allListFile[] = [
  {
    name: 'Fruit',
    children: [{name: 'Apple'}, {name: 'Banana'}, {name: 'Fruit loops'}],
  },
  {
    name: 'Vegetables',
    children: [
      {
        name: 'Green',
        children: [{name: 'Broccoli'}, {name: 'Brussels sprouts'}],
      },
      {
        name: 'Orange',
        children: [{name: 'Pumpkins'}, {name: 'Carrots'}],
      },
    ],
  },
];
//
interface ExampleFlatNode {
  expandable: boolean;
  name: string;
  level: number;
}

@Component({
  selector: 'app-list-bar',
  templateUrl: './list-bar.component.html',
  styleUrls: ['./list-bar.component.css'],
  standalone: true,
  imports: [MatTreeModule, MatButtonModule, MatIconModule],
})


export class ListBarComponent {

 public allListFile:string[] = [];

 private _transformer = (node: allListFile, level: number) => {
    return {
      expandable: !!node.children && node.children.length > 0,
      name: node.name,
      level: level,
    };
  };

  treeControl = new FlatTreeControl<ExampleFlatNode>(
    node => node.level,
    node => node.expandable,
  );

  treeFlattener = new MatTreeFlattener(
    this._transformer,
    node => node.level,
    node => node.expandable,
    node => node.children,
  );

  dataSource = new MatTreeFlatDataSource(this.treeControl, this.treeFlattener);

  constructor(private myServe:MySerService,private route:ActivatedRoute) {
    this.dataSource.data = TREE_DATA;
  }

  ngOnInit() {
    this.allListFile = [];
    this.myServe.getFileListToClient().subscribe(
      data => {
       this.allListFile = data;
       alert("get files sucssesfuly");
      },
      err => {
        alert(err.message);
      }
      );
  }

  hasChild = (_: number, node: ExampleFlatNode) => node.expandable;
}
