import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MatSidenavModule} from '@angular/material/sidenav';
import { SomeJpgFilesComponent } from "../some-jpg-files/some-jpg-files.component";
import { ListBarComponent } from "../list-bar/list-bar.component";
import { distinctUntilChanged, map } from 'rxjs';
import { MySerService } from 'src/app/Service/my-ser.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  standalone: true,
  imports: [MatSidenavModule, ListBarComponent],
  // template: 'The href is: {{href}}',
})


export class HomeComponent {
   public href: string = "";
  location:string="";
  constructor(private myServe:MySerService, private router: Router,private route:ActivatedRoute) {}

  // public path:string="";

  ngOnInit() {
      // this.href = this.router.url;
      // alert(this.router.url);
      //שמירת הנתונים מה/URL/במשתנה
      this.route.queryParams.pipe(
        map(params => params["path"]),
        distinctUntilChanged(),
      ).subscribe(changedParam => {
        alert(`reacting to single param change ${changedParam}`);
        this.sendlocation(changedParam);
      })
  }

  sendlocation(location:string)
  {
    this.location = "";
    this.myServe.SendLocationServe(this.location).subscribe(
      data => {
       alert("send location sucssesfuly");
      },
      err => {
        alert(err.message);
      }
      );

  }

}
